import React, { Component } from "react";
import { StyleSheet, Text, View } from "react-native";
import { Button, Row, Container, Col } from "react-bootstrap";
import { AnimatedCircularProgress } from "react-native-circular-progress";




export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fillTemp: 50,
      fillHumidity: 20,
    };
    this.setHumidity=this.setHumidity.bind(this);
    
  }

  setHumidity(x){
    //this.setState({fillHumidity:x});
    console.log("set to "+x);
  }

  

  syncronice = (state) => {
    // TODO: mqtt verbindung
    console.log("get from mqtt");
  };

  render() {
    return (
      <View style={styles.container}>
        <Container fluid>
          <Row>
            <Col>
              <Text style={styles.title}>Temperatur</Text>

              <AnimatedCircularProgress
                size={200}
                width={15}
                fill={this.state.fillTemp}
                tintColor="#00e0ff"
                backgroundColor="#3d5875"
              >
                {(fill) => (
                  <Text style={styles.title}>{this.state.fillTemp}</Text>
                )}
              </AnimatedCircularProgress>
            </Col>
          </Row>

          <Row>
            <Col>
              <Text style={styles.title}>Humidity</Text>

              <AnimatedCircularProgress
                size={200}
                width={15}
                fill={this.state.fillHumidity}
                tintColor="#00e0ff"
                backgroundColor="#3d5875"
              >
                {(fill) => (
                  <Text style={styles.title}>{this.state.fillHumidity}</Text>
                )}
              </AnimatedCircularProgress>
            </Col>
          </Row>

          <Row>
            <Button variant="primary" onClick={this.syncronice}>
              SYNCRONICE
            </Button>
          </Row>
        </Container>
      </View>
    );
  }
}

//Styles f√ºr aussehen
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    marginLeft: "auto",
    marginRight: "auto",
  },

  title: {
    marginLeft: "auto",
    marginRight: "auto",
    marginTop: 12,
    paddingVertical: 10,
    alignItems: "center",
    color: "#20232a",
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
    justifyContent: "center",
  },
});
