import React, {Component} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Button, Row, Container, Col} from 'react-bootstrap';
import { SliderPicker } from 'react-color';

export class Beleuchtung extends Component {
  
  state = {
    background: '#fff',
  };  

  handleChangeComplete = (color) => {
    this.setState({ background: color.hex });
    // console.log("Selected Color" + this.state.background )
  };

  callColor = (color) => {
    // TODO: mqtt verbindung zur LED lampe color muss von hex auf RGB 
    console.log("Selected Color" + this.state.background );
  }

    render() {
        return (
    
          <View> 
          <Container fluid>

            <Row>
              <Text style={styles.title}>Beleuchtung</Text>
            </Row>
         
            <SliderPicker style ={styles.container}
              color={ this.state.background }
              onChangeComplete={ this.handleChangeComplete && this.callColor }
              onPress={this.callColor}
            />
          </Container> 
          </View>
        );
      }
}

//Styles f√ºr aussehen
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  
    title: {
      marginLeft: "auto",
      marginRight: "auto",
      marginTop: 12,
      paddingVertical: 10,
      alignItems: 'center',
      color: "#20232a",
      textAlign: "center",
      fontSize: 30,
      fontWeight: "bold",
      justifyContent: 'center',
    },

    button: {
      padding: 50,
      textAlign: 50
    }
  });
