import React, {Component} from 'react';
import { StyleSheet, Text, View , TouchableOpacity, Alert} from 'react-native';
import { Button, Row, Container, Col} from 'react-bootstrap';
import { SliderPicker } from 'react-color';

export class Beleuchtung extends Component {
  
  state = {
    background: '#fff',
  };  

  handleChangeComplete = (color) => {
    this.setState({ background: color.rgb });
  };

  callColor(){
    console.log("Selected Color" + this.state)
  }

    render() {
        return (
    
          <View> 
          <Container fluid>

            <Row>
              <Col><Text style={styles.title}>Beleuchtung</Text></Col>
            </Row>

            <Row>
              <Button variant="outline-success">Success</Button>
            </Row>
          
            <SliderPicker style ={styles.container}
              color={ this.state.background }
              onChangeComplete={ this.handleChangeComplete && this.callColor }
              onPress={this.callColor}
            />
          
          </Container> 
          </View>
        );
      }
}

//Styles f√ºr aussehen
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  
    title: {
      marginTop: 16,
      paddingVertical: 8,
      color: "#20232a",
      textAlign: "center",
      fontSize: 30,
      fontWeight: "bold"
    }
  });
