import React, {Component} from 'react';
import { Beleuchtung } from "./Beleuchtung";
import { Navigation } from "./Navigation";
import { Home } from "./Home";
import {BrowserRouter, Route, Switch, Redirect} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import mqtt from "mqtt";

var client; //mqtt client object
var mqttBrokerIP="mqtt://34.107.25.251";
var portNum = 1880;

export default class reactApp extends Component{
  constructor() {
    super();
    this.state = {
      myText: "My Original Text",
      _humidity:0,
      data: 'Jordan Belfort'
    };
    //GLOBAL.screen1 = this;

  }

  componentWillMount() {  
    client = mqtt.connect(mqttBrokerIP,{
      /*clientId: 'clientId-hfwBPuPMQe'*/
      port: portNum,
      path: '/mqtt',
      protocolId: 'MQIsdp',
        protocolVersion: 3,
        connectTimeout:5000,
        //incomingStore:manager.incoming,
        //outgoingStore:manager.outgoing
      });


      client.on("connect",function(){
        console.log("Connected");
      });
  }


  render(){
    return(
      <BrowserRouter>
      <Navigation/>
      <div className="container">
        <Switch>
          <Route path='/Beleuchtung' component={Beleuchtung} />
          <Route path='/Home' component={Home} />
          <Redirect to='/Home' component={Home} />
        </Switch>
      </div>
      </BrowserRouter>
    );
  }
}
